package Primitives;

public class CharacterClass {

	public static void main(String[] args) {
		
		// TODO Auto-generated method stub
		char c='A';
		int y='G'-0;
		System.out.println(Character.getNumericValue(c));
		System.out.println(y);
		System.out.println(String.valueOf(123));
		System.out.println(Double.compare(102, 101.0));
		System.out.println(Math.abs(-7.555));
		System.out.println(Math.abs(7.555));
		System.out.println(Math.ceil(7.1));
		System.out.println(Math.floor(7.1));
		System.out.println(Math.round(7.1));
		System.out.println(Math.round(7.4999));
		System.out.println(Math.round(7.50));
	}

}
